import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';


function A() {
  return <div>
        <h2>Beat Maker</h2>
        {audioBeats.map()(clip => {
          <Pad clip={clip}/>
        })}
        </div>
}

function Pad({ clip }) {
  const playBeat = () => {
    const audioTag = document.getElementById(clip.keyTrigger);
    audioTag.currentTime = 0;
    audioTag.play();
  };

  
  return (
    <div onClick={playBeat} className="btn btn-secondary p-4 m-3">
      <audio className="clip" id={clip.keyTrigger} src={clip.url} />
      {clip.keyTrigger}
    </div>
  );
  }

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);

